 function validateSrjNumberInput(e){let t=e.target,r=t.value,a=r.replace(/\D/g,"");a=a.length>0&&"0"===a[0]?a.slice(0,11):a.slice(0,10),t.value=a}function validateSrjSpecialInput(e){let t=e.target;t.value=t.value.replace(/[^a-zA-Z0-9 ]/g,"")}const srjNumberInputs=document.querySelectorAll(".srjNumber");srjNumberInputs.forEach(e=>{e.addEventListener("input",validateSrjNumberInput)});const srjSpecialInputs=document.querySelectorAll(".srjSpecial");srjSpecialInputs.forEach(e=>{e.addEventListener("input",validateSrjSpecialInput)});
